    def write_number(self, number):
        # print(number)
        if self.label_result.text() == '0' or self.label_result.text()[0] == 'Р':
            self.label_result.setText(number)    
        else:
            self.label_result.setText(self.label_result.text() + number)

    def results(self):
        # eval работает со строками как с мат.выражением - '56+7' -> 56 + 7
        if not self.label_result.text()[0] == 'Р':
            res = eval(self.label_result.text())
            self.label_result.setText('Результат: ' + str(res))
        else:
            error = QMessageBox()
            error.setWindowTitle('Ошибка')
            error.setText('Сейчас это действие выполнить нельзя')
            error.setIcon(QMessageBox.Warning)
            error.setStandardButtons(QMessageBox.Reset|QMessageBox.Cancel|QMessageBox.Ok)
            error.setDefaultButton(QMessageBox.Cancel)
            error.setInformativeText('Два раза действие не выполнить')
            error.setDetailedText('Детали')

            error.buttonClicked.connect(self.popup_action)

            error.exec_()

    def popup_action(self, btn):
        if btn.text() == 'Ok':
            print('ok')
        elif btn.text() == 'Reset':
            self.label_result.setText('0')


    def cancel(self):
        self.label_result.setText('0')


